INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (512, 'JR', '', 'Описание Клюшка CCM RIBCOR TRIGGER 3D, топовая модель линейки CCM 2018 года. Клюшка имеет низкую точку прогиба OPTIMIZED LOW KICK. При производстве клюшки используется углеродное волокно SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая геометрия трубы POP MATRIX, которая улучшает передачу энергии и эффективность клюшки. Крюк имеет конструкцию ASCENT BLADE 3 , которая обеспечивает превосходное ощущение и высокую точность броска. Длина производителя - 40 FLEX - 49 дюйма; 50 FLEX - 52 дюйма Полная длина клюшки - 40 FLEX - 140 см; 50 FLEX - 147 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология трубы – X-FLOW; POP MATRIX • Технология крюка – ASCENT BLADE 3 • Материал - SIGMATEX Характеристики Возрастная группа JR Линейка RIBCOR', '/root/images/Клюшка/CCM_RIBCOR TRIGGER 3D PMT GRIP_JR.png', null, 'RIBCOR TRIGGER 3D PMT GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (513, 'JR', '', 'Описание Клюшка BAUER NEXUS S16 1N GRIPTAC Ваша цель — прострел верхнего края или нижнего угла? Новая клюшка 2017 NEXUS 1N не подведет вас даже на чемпионатах. Благодаря новой геометрии рукоятки для большего контроля и усовершенствованной технологии зоны Sweet Spot вы сможете без труда забросить шайбу прямо в сетку. Профиль прогиба: • Профиль прогиба посередине NEXUS TRU Mid-Kick Конструкция/технология изготовления рукоятки: • Выгнутые с двух сторон стенки • Технология зоны наилучшего восприятия • Технология eLASTech (только SR и INT) • Легковесная конструкция TeXtreme® • Технология Monocomp Конструкция/технология изготовления крюка: • Наружная часть: Легкая TeXtreme® конструкция • Внутренняя часть: Основа крюка 2 POWER SENSE • Профиль крюка Pure Shot Форма рукоятки: • Выгнутые с двух сторон стенки • Shaft Taper • Технология сужения для упрощения прострелов Варианты захвата: • Покрытие GRIPTAC • Матовая отделка Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS S16 1N GRIPTAC_JR.png', null, 'NEXUS S16 1N GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (514, 'INT', '', 'Описание Клюшка BAUER VAPOR 1X GRIPTAC Самая передовая и технологичная клюшка VAPOR на сегодняшний день. Созданная для молниеносных атак, VAPOR 1X с новой технологией Quick-Release Taper подарит вам новые ощущения на льду. Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: AERO SENSE 2 Core - NEW • Профиль крюка Pure Shot Форма рукоятки: • Круглая двояковогнутая рукоятка Конусность рукоятки • Технология сужения QRT (только SR и INT) - NEW Варианты захвата • Покрытие GRIPTAC • Также доступны варианты с матовой отделкой без зоны захвата Grip Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 1X GRIPTAC_INT.png', null, 'VAPOR 1X GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (515, 'SR', '', 'Описание Клюшка BAUER SUPREME 2S, профессиональная клюшка в линейки SUPREME. Главным преимуществом клюшки является эффективность и прочность. Чтобы сделать эту клюшку самой прочной в серии SUPREME производитель укрепил стенки рукоятки. Клюшка выполнена из углеродного волокна 3К, благодаря этому клюшка очень прочна. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. Материал крюка углеродное волокно 12К.Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. В качестве внутреннего сердечника крюка используется пена AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 64 дюйма Полная длина клюшки - 175 см Особенности: ✅Уровень – Профессиональный ✅Точка прогиба – Верхняя ✅Технология броска – MPK ✅Технология трубы – Карбон 3К ✅Технология крюка – Карбон 12К; MaxBalance; AERO FOAM I ✅Конструкция – High Performance mold ✅Геометрия шафта – Квадратная Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Клюшка/BAUER_SUPREME 2S GRIPTAC_SR.png', null, 'SUPREME 2S GRIPTAC', 1, 13);
