INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (521, 'JR', '', 'Описание Клюшка BAUER SUPREME 2S PRO, топовая клюшка в линейки SUPREME. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX , которая увеличивает прочность и долговечность. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. В крюке данной модели содержится гель RENEWCORE, который заполняет микротрещины и повышает прочность и долговечность клюшки. При производстве крюка так же используется технология ADVANCED CARBON LAYERING, обеспечивая более высокую плотность между слоями углеродного волокна, в результате чего уменьшается количество смолы, пропитывающей углеродное волокно, что приводит к снижению веса клюшки и лучшему сопротивлению ударным нагрузкам. Длина производителя - 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: • Уровень – Элитный • Точка прогиба – Верхняя • Технология броска – MPK • Технология трубы – TeXtreme; DURAFLEX • Технология крюка - TeXtreme; ACL • Конструкция – MONOCOMP • Геометрия шафта – Квадратная Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Клюшка/BAUER_SUPREME 2S PRO GRIPTAC_JR.png', null, 'SUPREME 2S PRO GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (522, 'SR', '', 'Описание Клюшка BAUER VAPOR 1X GRIPTAC Самая передовая и технологичная клюшка VAPOR на сегодняшний день. Созданная для молниеносных атак, VAPOR 1X с новой технологией Quick-Release Taper подарит вам новые ощущения на льду. Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: AERO SENSE 2 Core - NEW • Профиль крюка Pure Shot Форма рукоятки: • Круглая двояковогнутая рукоятка Конусность рукоятки • Технология сужения QRT (только SR и INT) - NEW Варианты захвата • Покрытие GRIPTAC • Также доступны варианты с матовой отделкой без зоны захвата Grip Длина производителя - 60 дюймов Полная длина клюшки - 166 см Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 1X GRIPTAC_SR.png', null, 'VAPOR 1X GRIPTAC', 1, 13);
