INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (525, 'SR', '', 'Описание Клюшка BAUER VAPOR 2X TEAM, полупрофессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба. Труба клюшки выполнена из углеродного волокна CARBON UD. Конструкция трубы MICRO FEEL II с двояковогнутыми боковыми стенками и скругленными углами, обеспечивает игроку максимальное удобство. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Крюк клюшки выполнен по технологии MaxBalance, благодаря чему повышается прочность крюка. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – CARBON UD; Micro Feel II • Технология крюка – 3K CARBON; MaxBalance; AERO FOAM I • Конструкция – HIGH PERFORMANCE MOLD • Геометрия шафта – С закругленными краями Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 2X TEAM GRIP_SR.png', null, 'VAPOR 2X TEAM GRIP', 1, 13);
