INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (527, 'INT', '', 'Описание Клюшка CCM JETSPEED FT2, топовая модель линейки JETSPEED 2019 года. Данная клюшка выполнена из углеродного волокна SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данное волокно имеет особое плетение, обеспечивая прочность и легкость клюшки. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Клюшка имеет гибридную точку прогиба, благодаря этому клюшка подходит, как для резких кистевых, так и для мощных бросков. Крюк клюшки имеет амортизированный сердечник JF-80, благодаря этому мы получаем сверхлегкий крюк с усиленными пяткой и носком. Длина клюшки: ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Гибридная ✅Технология трубы – X-FLOW ✅Технология крюка – JF-80 Характеристики Возрастная группа INT Линейка JETSPEED', '/root/images/Клюшка/CCM_JETSPEED FT2 GRIP_INT.png', null, 'JETSPEED FT2 GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (528, 'INT', '', 'Описание Клюшка CCM RIBCOR TRIGGER 3D, топовая модель линейки CCM 2018 года. Клюшка имеет низкую точку прогиба OPTIMIZED LOW KICK. При производстве клюшки используется углеродное волокно SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая геометрия трубы POP MATRIX, которая улучшает передачу энергии и эффективность клюшки. Крюк имеет конструкцию ASCENT BLADE 3 , которая обеспечивает превосходное ощущение и высокую точность броска. ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология трубы – X-FLOW; POP MATRIX • Технология крюка – ASCENT BLADE 3 • Материал - SIGMATEX Характеристики Возрастная группа INT Линейка RIBCOR', '/root/images/Клюшка/CCM_RIBCOR TRIGGER 3D PMT_INT.png', null, 'RIBCOR TRIGGER 3D PMT', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (529, 'INT', '', 'Описание Клюшка BAUER VAPOR 2X TEAM, полупрофессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба. Труба клюшки выполнена из углеродного волокна CARBON UD. Конструкция трубы MICRO FEEL II с двояковогнутыми боковыми стенками и скругленными углами, обеспечивает игроку максимальное удобство. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Крюк клюшки выполнен по технологии MaxBalance, благодаря чему повышается прочность крюка. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – CARBON UD; Micro Feel II • Технология крюка – 3K CARBON; MaxBalance; AERO FOAM I • Конструкция – HIGH PERFORMANCE MOLD • Геометрия шафта – С закругленными краями Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 2X TEAM GRIP_INT.png', null, 'VAPOR 2X TEAM GRIP', 1, 13);
