INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (552, 'INT', '', 'Описание Клюшка WARRIOR COVERT QRE, топовая модель линейки COVERT 2018 года. Особенности: • EDGE QUICK RELEASE – Клюшки данной серии имеют максимально низкую точку прогиба SUPER LOW KICK для быстрых и резких бросков • EDGE TAPER – Уникальный профиль клюшки обеспечивает один из самых быстрых и точных бросков, благодаря ромбовидной форме сужения клюшки • MINIMUS CARBON 1200 - Высокопрочная, надежная и легкая конструкция из цельнолитого углеродистого волокна. Новая геометрия края клюшки в момент броска, мгновенно и точно передает энергию на крюк, бросок получается более резкий и мощный. • TRUE 1 PHANTOM – Клюшка имеет полностью монолитную конструкцию и отличается хорошей сбалансированностью и прочностью, увеличивает чувство владения клюшкой во время игры, дает вам больше контроля и реакции • PRO CORTEX GRIP – Нескользящее текстурированное мягкое покрытие клюшки- легко удерживать в руках для оптимального контроля Характеристики Возрастная группа INT Линейка COVERT', '/root/images/Клюшка/WARRIOR_COVERT QRE GRIP_INT.png', null, 'COVERT QRE GRIP', 3, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (553, 'SR', '', 'Описание Клюшка BAUER NEXUS 2N, профессиональная клюшка в линейки NEXUS. Главным преимуществом клюшки является эффективность и прочность. Клюшка выполнена из высококачественного углеродного волокна 12К и имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD, благодаря этому клюшка считается очень отзывчивой, легкой и прочной. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. Длина производителя - 64 дюйма Полная длина клюшки - 175 см Особенности: • Уровень – Профессиональный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – Карбон 12К • Технология крюка – Карбон 12К; MaxBalance; R-LITE • Конструкция – HIGH PERFORMANCE MOLD Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N GRIPTAC_SR.png', null, 'NEXUS 2N GRIPTAC', 1, 13);
