INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (555, 'SR', '', 'Описание Клюшка TRUE A6.0 HT GRIP ''18 SR – клюшка для мощного броска. Сделана для игроков, которым нужна настоящая взрывная сила из любой позиции для броска. Новая линейка ASERIES ’18, как инженерное чудо, которое позволяет лидировать в моменте инерции SWING WEIGHT, среди всех лидеров в производстве клюшек. Игровые преимущества: - НИЖНЯЯ ТОЧКА ПРОГИБА: Точка прогиба в нижней трети вала у соединения с пером для быстрой загрузки и выполнения кистевых бросков wrist и snapshots - ПРОЧНОСТЬ: Технология BRT+ позволяет создать более легкое и прочное перо, позволяющее нам перераспределить вес в вал для экстремальной силы удара и общей долговечности клюшки. - БАЛАНС: Лидирующая в области цельная конструкция Axenic позволяет оптимизировать распределение веса по всей клюшке для непревзойденного баланса. - Мощность: Оптимизированная для более легкого получения энергии загрузки, новая ASERIES ’18 обеспечивает взрывную мощность от броска из любой позиции. - ПЕРО: Самое прочное, самое стойкое перо, которое TRUE когда либо создавали, сочетающее в себе мощность и долгий сроки жизни. - ВЕС: 415 гр. (85 Flex) Характеристики Возрастная группа SR', '/root/images/Клюшка/TRUE_A6.0 HT GRIP 18_SR.png', null, 'A6.0 HT GRIP 18', 16, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (556, 'SR', '', 'Описание Клюшка TRUE A6.0 SBP GRIP ''18 SR – клюшка для мощного броска. Сделана для игроков, которым нужна настоящая взрывная сила из любой позиции для броска. Новая линейка ASERIES ’18, как инженерное чудо, которое позволяет лидировать в моменте инерции SWING WEIGHT, среди всех лидеров в производстве клюшек. Игровые преимущества: - СРЕДНЯЯ ТОЧКА ПРОГИБА: Точка прогиба расположена чуть ниже нижней руки, с более жесткой нижней частью вала для упрощения загрузки и более мощных бросков. - ПРОЧНОСТЬ: Технология BRT+ позволяет создать более легкое и прочное перо, позволяющее нам перераспределить вес в вал для экстремальной силы удара и общей долговечности клюшки. - БАЛАНС: Лидирующая в области цельная конструкция Axenic позволяет оптимизировать распределение веса по всей клюшке для непревзойденного баланса. - Мощность: Оптимизированная для более легкого получения энергии загрузки, новая ASERIES ’18 обеспечивает взрывную мощность от броска из любой позиции. - ПЕРО: Самое прочное, самое стойкое перо, которое TRUE когда либо создавали, сочетающее в себе мощность и долгий сроки жизни. - ВЕС: 415 гр. (85 Flex) Характеристики Возрастная группа SR', '/root/images/Клюшка/TRUE_A6.0 SBP GRIP 18_SR.png', null, 'A6.0 SBP GRIP 18', 16, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (557, 'SR', '', 'Описание Клюшка BAUER VAPOR S18 1X LITE GRIPTAC Абсолютно новая клюшка Vapor 1X LITE изготовлена для оптимизации вашей скорости с помощью нашего самого легкого крюка. Изготовленная с использованием технологии QRT Plus и усовершенствованного углеродного наслоения (ACL), эта клюшка предназначена для элитных хоккеистов, чтобы они смогли пролететь через защитников и заставить вратарей гадать, куда придется еще более точный бросок, чем раньше. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Технология eLASTech (только SR и INT) • Легкая конструкция TeXtreme® • Технология Monocomp Конструкция/технология изготовления крюка: • Наружная часть: технология изготовления крюка ACL, легкая TeXtreme® конструкция, матовый крюк с текстурой • Внутренняя часть: основа крюка AERO SENSE 2 • Профиль крюка Pure Shot Форма рукоятки: • Размеры рукоятки Micro Feel II и выгнутые с двух сторон стенки • Конусность рукоятки • Технология QRT+ (только SR и INT) Варианты захвата: • Покрытие GRIPTAC Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR S18 1X LITE GRIPTAC_SR.png', null, 'VAPOR S18 1X LITE GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (558, 'JR', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme • Технология крюка - ACL; R-LITE • Конструкция – MONOCOMP Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N PRO GRIPTAC_JR.png', null, 'NEXUS 2N PRO GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (559, 'JR', '', 'Описание Клюшка BAUER VAPOR FLYLITE JR, топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE! Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 30 FLEX - 50 дюймов; 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 30 FLEX - 139 см; 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология броска – XE TAPER ✅Технология трубы – TeXtreme; DURAFLEX; ACL ✅Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE ✅Конструкция – MONOCOMP ✅Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR FLYLITE GRIP_JR.png', null, 'VAPOR FLYLITE GRIP', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (560, 'INT', '', 'Описание Клюшка WARRIOR ALPHA DX, топовая модель линейки ALPHA 2019 года. Особенности: ✅ SABER TAPER – Уникальная конструкция клиновидного сужения создает жесткую к скручиванию зону прогиба клюшки, что делает крюк более стабильным для большой точности удара. Улучшенная конструкция легко задействует прогиб и обеспечивает быстрый высокоточный релиз. ✅ MINIMUS CARBON 1400 – Карбоновая конструкция с плоским плетением, благодаря чему клюшка стала более надежной и легкой. Кроме того, этот материал стал более динамичным и эластичным. ✅ TRUE 1 PHANTOM – Улучшенный баланс и отклик заметно повышают чувствительность и контроль шайбы. Самая легкая и мощная цельнолитая клюшка TRUE 1 ✅ FUELCORE – Легкий полимерный сердечник крюка усиливает ощущение шайбы и делает броски сильнее. Более прочные и жесткие материалы увеличивают долговечность крюка. ✅ ERGO SHAFT SHAPE – Эргономичная форма шафта клюшки удобно и надежно ложится в руки, повышая чувствительность, чтобы вы могли легко совершать удары, финты и убирать шайбу под себя. ✅ APEX GRIP TEXTURE – Текстурированное нескользящее покрытие клюшки обеспечивает максимальный контроль и ощущение. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Характеристики Возрастная группа INT Линейка ALPHA', '/root/images/Клюшка/WARRIOR_ALPHA DX GRIP_INT.png', null, 'ALPHA DX GRIP', 3, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (561, 'SR', '', 'Описание Клюшка CCM SUPER TACKS AS2 PRO, топовая модель линейки TACKS 2019 года. Данная клюшка выполнена из углеродного волокна SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данное волокно имеет особое плетение, обеспечивая прочность и легкость клюшки. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Клюшка имеет среднюю точку прогиба, обеспечивая лучшую стабильность и контроль во время мощных бросков. Крюк ACU4 имеет легкую конструкцию и обеспечивает максимальный контроль и ощущение во время броска. Так же крюк имеет усиленную пятку и носок для максимальной прочности. Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Средняя ✅Технология трубы – X-FLOW ✅Технология крюка – ACU4 ✅Материал - SIGMATEX Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Клюшка/CCM_SUPER TACKS AS2 PRO GRIP_SR.png', null, 'SUPER TACKS AS2 PRO GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (562, 'INT', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme; DURAFLEX(INT-SR) • Технология крюка - ACL; R-LITE; eLASTech(INT-SR) • Конструкция – MONOCOMP Характеристики Возрастная группа INT Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N PRO GRIPTAC_INT.png', null, 'NEXUS 2N PRO GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (563, 'SR', '', 'Описание Клюшка BAUER VAPOR 2X PRO, профессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба, и она выше на 5 см, чем стандартные клюшки. Труба и крюк покрыты высококачественным углеродным волокном TeXtreme, благодаря этому клюшка очень прочна и имеет малый вес. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Длина производителя - 62 дюйма Полная длина клюшки - 170 см Особенности: • Уровень – Профессиональный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme • Технология крюка – TeXtreme; MaxBalance • Конструкция – High Performance mold • Геометрия шафта – С закругленными краями Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 2X PRO GRIP_SR.png', null, 'VAPOR 2X PRO GRIP', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (564, 'INT', '', 'Описание Клюшка CCM RIBCOR TRIGGER 4 PRO, топовая модель линейки CCM 2019 года. При производстве клюшки используется высокотехнологичное углеродное волокно SIGMATEX 18К, делая клюшку невероятно легкой и прочной. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая конструкция ERGONOMIC SHAFT DESIGN обеспечивает разную форму шафта по всей длине клюшки, придавая максимальное удобство и комфорт во время игры. Клюшка имеет нижнюю точку прогиба с новым ассиметричным тэйпером, который предполагает традиционную вогнутость с внутренней стороны, обеспечивая максимальную загрузку клюшки и наличие выпуклой геометрии с внешней стороны тэйпера для большей точности бросков и контроля шайбы. Новый крюк ADGILITY BLADE имеет мягкую конструкцию для более быстрых и резких бросков. ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология трубы – X-FLOW; ERGONOMIC SHAFT DESIGN ✅Технология крюка – ADGILITY BLADE ✅Материал – SIGMATEX 18К Характеристики Возрастная группа INT Линейка RIBCOR', '/root/images/Клюшка/CCM_RIBCOR TRIGGER 4 PRO GRIP_INT.png', null, 'RIBCOR TRIGGER 4 PRO GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (565, 'SR', '', 'Описание Клюшка WARRIOR ALPHA DX, топовая модель линейки ALPHA 2019 года. Особенности: ✅ SABER TAPER – Уникальная конструкция клиновидного сужения создает жесткую к скручиванию зону прогиба клюшки, что делает крюк более стабильным для большой точности удара. Улучшенная конструкция легко задействует прогиб и обеспечивает быстрый высокоточный релиз. ✅ MINIMUS CARBON 1400 – Карбоновая конструкция с плоским плетением, благодаря чему клюшка стала более надежной и легкой. Кроме того, этот материал стал более динамичным и эластичным. ✅ TRUE 1 PHANTOM – Улучшенный баланс и отклик заметно повышают чувствительность и контроль шайбы. Самая легкая и мощная цельнолитая клюшка TRUE 1 ✅ FUELCORE – Легкий полимерный сердечник крюка усиливает ощущение шайбы и делает броски сильнее. Более прочные и жесткие материалы увеличивают долговечность крюка. ✅ ERGO SHAFT SHAPE – Эргономичная форма шафта клюшки удобно и надежно ложится в руки, повышая чувствительность, чтобы вы могли легко совершать удары, финты и убирать шайбу под себя. ✅ APEX GRIP TEXTURE – Текстурированное нескользящее покрытие клюшки обеспечивает максимальный контроль и ощущение. Полная длина клюшки - 165 см Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Клюшка/WARRIOR_ALPHA DX GRIP_SR.png', null, 'ALPHA DX GRIP', 3, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (566, 'SR', '', 'Описание Клюшка WARRIOR COVERT QRE, топовая модель линейки COVERT 2018 года. Особенности: • EDGE QUICK RELEASE – Клюшки данной серии имеют максимально низкую точку прогиба SUPER LOW KICK для быстрых и резких бросков • EDGE TAPER – Уникальный профиль клюшки обеспечивает один из самых быстрых и точных бросков, благодаря ромбовидной форме сужения клюшки • MINIMUS CARBON 1200 - Высокопрочная, надежная и легкая конструкция из цельнолитого углеродистого волокна. Новая геометрия края клюшки в момент броска, мгновенно и точно передает энергию на крюк, бросок получается более резкий и мощный. • TRUE 1 PHANTOM – Клюшка имеет полностью монолитную конструкцию и отличается хорошей сбалансированностью и прочностью, увеличивает чувство владения клюшкой во время игры, дает вам больше контроля и реакции • PRO CORTEX GRIP – Нескользящее текстурированное мягкое покрытие клюшки- легко удерживать в руках для оптимального контроля Полная длина клюшки - 165 см Характеристики Возрастная группа SR Линейка COVERT', '/root/images/Клюшка/WARRIOR_COVERT QRE GRIP_SR.png', null, 'COVERT QRE GRIP', 3, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (567, 'INT', '', 'Описание Клюшка BAUER VAPOR FLYLITE, топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE! Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme; DURAFLEX; ACL • Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE • Конструкция – MONOCOMP • Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR FLYLITE GRIP_INT.png', null, 'VAPOR FLYLITE GRIP', 1, 13);
