INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (574, 'JR', '', 'Описание Клюшка BAUER SUPREME ADV, ограниченная серия клюшек линейки SUPREME 2019 года. Представляем вашего вниманию самую легкую клюшку в истории SUPREME. Вес данной модели всего 375 грамм достигается за счет нового уникального семиугольного сечения трубки SONIC TAPER TECHNOLOGY. Новая геометрия тэйпера помогла не только снизить вес клюшки, а также добавить стабильности и мощи при каждом броске. По периметру всей клюшки используется технология углового переплетения карбона ACL. В крюке в качестве внутренней сердцевины используется суперлегкая жесткая пена, которая уменьшает вес крюка и повышает прочность. Так же для более быстрого владения шайбой в середине крюка используется амортизирующая пена. Клюшка имеет оптимизированную точку прогиба, позволяющая прикладывать минимум усилий для максимально мощного броска. Длина производителя - 54 дюймов Полная длина клюшки - 150 см Основные особенности: ✅Уровень – Элитный ✅Точка прогиба – Верхняя, оптимизированная ✅Тэйпер - SONIC TAPER TECHNOLOGY ✅Материал трубы – ADVANCED CARBON LAYERING ✅Крюк - ADVANCED CARBON LAYERING; амортизирующая пена, для более быстрого владения; суперлегкая, жесткая пена, для прочности и минимального веса Внимание: На данный товар скидка не действует. Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Клюшка/BAUER_SUPREME ADV SERIES GRIP_JR.png', null, 'SUPREME ADV SERIES GRIP', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (575, 'SR', '', 'Описание Клюшка BAUER VAPOR FLYLITE SHADOW SERIES, лимитированная, топовая модель линейки VAPOR 2019 года. Клюшка представлена полностью в черном цвете и выпущена в ограниченному количестве. Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme; DURAFLEX; ACL • Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE • Конструкция – MONOCOMP • Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR FLYLITE SHADOW SERIES_SR.png', null, 'VAPOR FLYLITE SHADOW SERIES', 1, 13);
