INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (608, 'SR', '', 'Описание Клюшка вратаря BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Нижняя часть клюшки выполнена из материала TeXtreme. Крюк данной модели изготовлен из пены AERO FOAM 3.Технология ERGO SPINE характеризуется трехмерной формой, благодаря которой клюшка является самой легкой и сбалансированной на рынке в данный момент. Помимо этого чтобы клюшка получилась достаточно легкой, ее черенок выполнен из углеродного волокна 12К, которое так же делает клюшку прочнее. Особенности: • Уровень – Элитный • Шафт – карбон 12К; Зона контроля GRIPTAC; двойная вогнутая геометрия • Крюк – AERO FOAM 3 • Расширенная часть – ERGO SPINE; TeXtreme Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Вратарская клюшка/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 14);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (609, 'SR', '', 'Описание Клюшка вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Данная клюшка представлена с крюком TECH STREAM, внутренняя часть которого выполнена из материала AERO FOAM 3 благодаря чему эта клюшка лучше поглощает удары и отличается меньшим весом в сравнении с предыдущей моделью этого поколения 1X. В расширенной части используется материал ADVANCED CARBON LAYERING и проходит воздушный канал, поэтому клюшка стала более легкой, при этом не потеряв своей прочности. А материал TeXtreme® в черенке клюшки обеспечивает превосходную передачу тактильных ощущений. Особенности: • Уровень – Элитный • Шафт – Композит • Крюк – TeXtreme®; AERO FOAM 3 • Расширенная часть – ACL Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Вратарская клюшка/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 14);
