INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (691, 'JR', '', 'Описание Наплечник вратаря CCM PREMIER R1.5, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель предназначена для вратарей, которые хотят оставить, как можно меньше места в воротах. Производитель создал квадратные рукава, чтобы облегчить движения рук вдоль тела, а также оптимизировал ширину, чтобы обеспечить максимальную зону охвата. Область рук и груди выполнена из высококачественного полиэтилена, который обеспечивает отличный уровень защиты. Для защиты плеч используется пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Область живота имеет сегментированные вставки из высококачественного полиэтилена, которые обеспечивают максимальную подвижность и хорошие защитные свойства. Система регулировки наплечника позволяет Вам, максимально комфорта настроить его под себя. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен; JDP ✅Защита плеч – Пена высокой плотности ✅Защита живота – Сегментированные вставки из полиэтилена Характеристики Возрастная группа JR Линейка PREMIER', '/root/images/Нагрудник вратаря/CCM_PREMIER R1.5 LE_JR.png', null, 'PREMIER R1.5 LE', 2, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (692, 'JR', '', 'Описание Наплечник вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Данный наплечник оснащен системой ARCH ADJUST, благодаря чему вратарь может регулировать его в верхней части плеч. Новая конструкция FREE FLEX, использованная в рукавах, придает наплечнику большую гибкость. Так же в наплечнике использована новая система регулировки длины рукава и торсовой части, которая придает наплечнику лучшую мобильность и максимальный комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Регулировка – Два эластичных ремня в области спины ✅Защита плеч – Пена высокой плотности ✅Защита рук – Пена высокой плотности; FREE FLEX ✅Защита груди – Пена высокой плотности ✅Защита ребер – Пена высокой плотности ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Нагрудник вратаря/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (693, 'JR', '', 'Описание Наплечник вратаря CCM EXTREME FLEX SHIELD E2.5, юниорская профессиональная модель линейки EXTREME FLEX 2018 года. Наплечник обладает максимальной мобильностью и отличными защитными функциям. Все защитные элементы наплечника выполнены из высококачественного полиэтилена, благодаря этому наплечник имеет легкий вес и подвижность при максимальной защите. Данная модель имеет систему регулировки VELCO ADJUSTMENT, благодаря чему наплечник можно максимально подогнать по телу. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен ✅Защита плеч – Полиэтилен ✅Защита живота – Сегментированная конструкция из полиэтилена Характеристики Возрастная группа JR Линейка EXTREME FLEX', '/root/images/Нагрудник вратаря/CCM_EXTREME FLEX SHIELD E2.5_JR.png', null, 'EXTREME FLEX SHIELD E2.5', 2, 20);
