INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (703, 'SR', '', 'Описание Наплечник вратаря BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. За счет уменьшения плечевых чашек, наплечник получился очень мобильным. В области плеча использована пена POWERLITE, который представляет собой смесь вспененного пропилена, пены высокой плотности и композитного материала CURV®, обеспечивающий защитные свойства. Защита локтя выполнена из пены PORON XRD для лучшего поглощения удара. Так же эта пена использована во всей области защиты рук. Внутренняя подкладка 37,5®, которая обработана по технологии SANITIZED®, для предотвращения развития бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Регулировка – Два эластичных ремня в области спины • Защита плеч – POWERLITE • Защита рук – PORON XRD; POWERLITE • Защита груди – VENT ARMOR • Защита ребер – Пена высокой плотности • Подкладка – 37,5®; SANITIZED® Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник вратаря/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (704, 'SR', '', 'Описание Наплечник вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. В сравнении с предыдущей модель 1Х, наплечник 2Х PRO претерпел значительные изменения. Рукава теперь отличаются трехточечной системой регулировки, за счет чего достигается большая амплитуда движения. В области рукавов используется композитный материал CURV®, который придает наплечнику очень легкий вес и хорошие защитные свойства. А плечевые чаши выполнены из пены AEROLITE. Помимо этого, наплечник оснащен системой ARCH ADJUST, посредством которой его можно подогнать по фигуре, отрегулировав спереди и сзади. Особенности: ✅Уровень – Элитный ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – AEROLITE ✅Защита рук – CURV®; FREE FLEX ✅Защита груди – CURV® ✅Защита ребер – Пена высокой плотности ✅Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Нагрудник вратаря/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (705, 'SR', '', 'Описание Наплечник вратаря CCM PREMIER PRO LE, топовая модель линейки PREMIER 2019 года. Данная модель предназначена для вратарей, которые хотят оставить, как можно меньше места в воротах. Производитель создал квадратные рукава, чтобы облегчить движения рук вдоль тела, а также оптимизировал ширину, чтобы обеспечить максимальную зону охвата. Область рук и груди выполнена из пены D3O, которая обеспечивает отличный уровень защиты, за счет максимального поглощения энергии удара. Для защиты плеч используется пена высокой плотности с вставкой из мягкой кожи. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Область живота имеет сегментированные вставки, которые обеспечивают максимальную подвижность. Система регулировки наплечника позволяет Вам, максимально комфорта настроить его под себя. Особенности: ✅Уровень – Элитный ✅Защита груди – D3O ✅Защита рук и локтя – D3O; JDP ✅Защита плеч – Пена высокой плотности Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Нагрудник вратаря/CCM_PREMIER PRO LE_SR.png', null, 'PREMIER PRO LE', 2, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (706, 'SR', '', 'Описание Наплечник вратаря BAUER SUPREME ULTRASONIC, топовая модель линейки SUPREME 2020 года. За счет уменьшения плечевых чашек, наплечник получился очень мобильным. В области плеча использована пена SHOCKLITE, которая обеспечивает наплечнику максимальную защиту при минимальном весе и композитный материал CURV®, так же обеспечивающий защитные свойства. Рукава выполнены из высокотехнологичной пены SHOCKLITE PRO, обеспечивающая лучшую мобильность. Область грудины выполнена из смеси высокотехнологичных пен SHOCKLITE и PORON XRD, которая максимально поглощает энергию удара. Внутренняя подкладка выполнена с применением технологии THERMO CORE, которая эффективно отводит влагу, предотвращает рост бактерий и возникновение неприятного запаха. Наплечник имеет анатомическую посадку. Особенности: ✅Уровень – Элитный ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – SHOCKLITE; CURV ✅Защита рук – SHOCKLITE PRO ✅Защита груди – SHOCKLITE; PORON XRD ✅Защита ребер – Пена высокой плотности ✅Подкладка – THERMO CORE Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник вратаря/BAUER_SUPREME ULTRASONIC_SR.png', null, 'SUPREME ULTRASONIC', 1, 20);
