INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (713, 'JR', '', 'Описание Трусы вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Любительский • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – MD FOAM • Защита спины – Пена • Подкладка – Влагоотталкивающая Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME S27_JR.png', null, 'SUPREME S27', 1, 21);
