INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (715, 'JR', '', 'Описание Трусы вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра- Пена высокой плотности; ✅Защита спины- FREE FLEX; DINAMIC 2.FLEX ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Трусы вратаря/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (716, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Любительский • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – MD FOAM • Защита спины – Пена • Подкладка – Влагоотталкивающая Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME S27_SR.png', null, 'SUPREME S27', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (717, 'JR', '', 'Описание Трусы вратаря CCM PREMIER R1.5 LE, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Все защитные элементы выполнены из высококачественного полиэтилена. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа JR Линейка PREMIER', '/root/images/Трусы вратаря/CCM_PREMIER R1.5 LE_JR.png', null, 'PREMIER R1.5 LE', 2, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (718, 'INT', '', 'Описание Трусы вратаря BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Защита тазобедренных суставов имеет плавающую конструкцию и выполнена с использованием пены PORON XRD, которая эффективно поглощает энергию удара, создавая отличную защиту. Набедренные щитки обладают отличными защитными свойствами, точно повторяют анатомические контуры тела, обеспечивая комфортную посадку и плотное прилегание элементов защиты. Внутренняя подкладка выполнена из материала THERMO MAX. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Полупрофессиональный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – PORON XRD • Защита спины – Пена • Подкладка – THERMO MAX Характеристики Возрастная группа INT Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME S29_INT.png', null, 'SUPREME S29', 1, 21);
