INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (720, 'JR', '', 'Описание Трусы вратаря CCM EXTREME FLEX SHIELD E2.5, юниорская профессиональная модель линейки EXTREME FLEX 2018 года. Данная модель имеет такую же конструкцию, как линейка PREMIER в области талии и ниже. Трусы линейки EXTREME FLEX более плотней прилегают к талии и сидят как влитые, что намного лучше с точки зрения комфорта. С изнанки предусмотрена специальная система, благодаря которой шорты сидят очень плотно на бедрах. В области тазобедренных суставах используется сегментированная пена высокой плотности. Для максимальной защиты в области бедра используется пластик с вставками из полиэтилена. Главное отличие от PREMIER заключается в том, как сидят трусы в области талии. Так же трусы имеют систему регулировки, благодаря которой трусы можно опустить на 1 дюйм (2,5СМ). Чехол в данной модели выполнены из нейлона плотность 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; полиэтилен ✅Защита тазобедренная – Пена высокой плотности ✅Защита позвоночника – Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); внутренние ремни по объему ✅Чехол –Нейлон 400 ден Характеристики Возрастная группа JR Линейка EXTREME FLEX', '/root/images/Трусы вратаря/CCM_EXTREME FLEX SHIELD E2.5_JR.png', null, 'EXTREME FLEX SHIELD E2.5', 2, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (721, 'INT', '', 'Описание Трусы вратаря CCM PREMIER R1.9 LE, профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Все защитные элементы выполнены из высококачественного полиэтилена. Область позвоночника выполнена из материала EVA. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Защита позвоночника - EVA ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа INT Линейка PREMIER', '/root/images/Трусы вратаря/CCM_PREMIER R1.9 LE_INT.png', null, 'PREMIER R1.9 LE', 2, 21);
