INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (726, 'SR', '', 'Описание Трусы вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра- Пена высокой плотности; ✅Защита спины- FREE FLEX; DINAMIC 2.FLEX ✅Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы вратаря/BAUER_VAPOR X2.9_SR.png', null, 'VAPOR X2.9', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (727, 'INT', '', 'Описание Трусы вратаря CCM EXTREME FLEX SHIELD E2.9, профессиональная модель линейки EXTREME FLEX 2018 года. Данная модель имеет такую же конструкцию, как линейка PREMIER в области талии и ниже. Трусы линейки EXTREME FLEX более плотней прилегают к талии и сидят как влитые, что намного лучше с точки зрения комфорта. С изнанки предусмотрена специальная система, благодаря которой шорты сидят очень плотно на бедрах. В области тазобедренных суставах используется сегментированная пена высокой плотности. Для максимальной защиты в области бедра используется пластик с вставками из полиэтилена. Главное отличие от PREMIER заключается в том, как сидят трусы в области талии. Так же трусы имеют систему регулировки, благодаря которой трусы можно опустить на 1 дюйм (2,5СМ). Чехол в данной модели выполнены из нейлона плотность 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; полиэтилен ✅Защита тазобедренная – Пена высокой плотности ✅Защита позвоночника – Пена высокой плотности ✅Регулировка – По длине на 1 дюйм (2,5 см); внутренние ремни по объему ✅Чехол –Нейлон 400 ден Характеристики Возрастная группа INT Линейка EXTREME FLEX', '/root/images/Трусы вратаря/CCM_EXTREME FLEX SHIELD E2.9_INT.png', null, 'EXTREME FLEX SHIELD E2.9', 2, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (728, 'SR', '', 'Описание Трусы вратаря CCM PREMIER R1.9 LE, профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Все защитные элементы выполнены из высококачественного полиэтилена. Область позвоночника выполнена из материала EVA. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Защита позвоночника - EVA ✅Регулировка – Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Трусы вратаря/CCM_PREMIER R1.9 LE_SR.png', null, 'PREMIER R1.9 LE', 2, 21);
