INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (730, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME 2S, топовая модель линейки SUPREME 2018 года. Для защиты бедер использована пена POWERLITE, это смесь пены высокой плотности, пены EPP и композитного материала CURV, обеспечивающая вратарю надежную защиту. В области таза использован материал XRD для того чтоб вратарь был всегда защищен во время перемещений. Внутри трусов находится специальный пояс, используя который вратарь может наиболее удобным для себя образом отрегулировать эти трусы в области талии. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Внутренняя подкладка трусов 37,5, которая обработана по технологии SANITIZED. Особенности: • Уровень – Элитный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – POWERLITE • Защита спины – POWERLITE • Подкладка – 37,5; SANITIZED Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (731, 'SR', '', 'Описание Трусы вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Также трусы оснащены системой EXTENSION FLEX, которая представляет собой застежку-молнию, посредством которой шорты можно удлинить на 1 дюйм(2.5см). Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. В области бедра используется композитный материал CURV, обеспечивающий повышенную прочность и надежную защиту. Особенности: ✅Уровень – Элитный ✅Регулировка – Система удлинения на 1 дюйм (2.5 см) ✅Защита бедра- CURV ✅Защита спины- FREE FLEX; AEROLITE ✅Подкладка – THERMOCORE Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы вратаря/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 21);
