INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (744, 'SR', '', 'Описание Коньки вратаря BAUER SUPREME 3S PRO, полупрофессиональная модель линейки SUPREME 2020 года. Внешний материал ботинка изготовлен из композитного материала 3D CURV, благодаря чему конек имеет легкий вес и хорошие защитные свойства. Интегрированная подошва обеспечивает максимальную передачу энергии. Внутренняя подкладка AEROFOAM увеличивает комфорт. Язык выполнен из войлока плотностью 48 унции. Коньки оснащены стаканами VERTEXX EDGE и лезвиями LS 3G+. Обновленная форма носка и лезвия LS 3G+ позволят увеличить угол атаки. Особенности: ✅Уровень – Полупрофессиональный ✅Внешний материал – 3D CURV ✅Язык – Войлочный плотностью 48 унций ✅Подошва – PRO TPU ✅Стакан – VERTEXX EDGE ✅Лезвие – LS 3G+ ✅Петля LUNDQVUST – Есть ✅Подкладка – AEROFOAM; GRIP LINER Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Коньки вратаря/BAUER_SUPREME 3S PRO_SR.png', null, 'SUPREME 3S PRO', 1, 22);
