INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (751, 'YTH', '', 'Описание Щитки вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Щитки подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал щитков SPEEDSKIN, который обеспечивает максимальное скольжения. Щиток имеет хорошую гибкую конструкцию с одним изломом. С обратной стороны расположена быстросъемная система ремней. Колено в данной модели регулируемое, благодаря этому вы можете брать щитки на вырост. Особенности: ✅Возраст – Дети (4-7 лет) ✅Конструкция – Мягкая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа YTH', '/root/images/Щитки вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (752, 'JR', '', 'Описание Щитки BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Щитки имеют более жесткую конструкцию с одним изломом под коленом, чем предыдущая коллекция, благодаря чему повысилась прочность и долговечность. Так же щитки имеют систему регулировки CRS для лучшего удобства. Крепление на носке смещено, что обеспечивает вратарю, улучшенный угол атаки. Внутренняя подкладка из влагоотталкивающего нейлона. Особенности: • Уровень – Любительский • Конструкция – Жесткая, с одним изломом под коленом • Материал – Искусственная кожа • Ремни – CRS • Колено – Конструкция AKL • Голень – Нейлон Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME S27_JR.png', null, 'SUPREME S27', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (753, 'JR', '', 'Описание Щитки BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Данные модель имеет конструкцию DINAMIC FLEX и ботинок с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Любительский ✅Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – Модернизированный синтетический ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Щитки вратаря/BAUER_VAPOR X2.7_JR.png', null, 'VAPOR X2.7', 1, 23);
