INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (774, 'SR', '', 'Описание Шлем вратаря BAUER NME VTX, топовая модель линейки NME 2018 года. Внешнее покрытие шлема выполнено из углеродного волокна TeXtreme, которое обладает отличными защитными свойствами и имеет очень легкий вес. Внутренняя подкладка пена PORON XRD, которая поглощает энергию и технология VTX. Подбородник так же выполнен из пены PORON XRD. Подкладка на внутреннем вкладыше в области лба с технологией 37,5 препятствует попаданию влаги в глаза и быстро сохнет. Особенности: • Уровень – Элитный • Внешнее покрытие – TeXtreme • Ключевые технологии – VTX; XRD PORON; TeXtreme; THERMOFORMED IX Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_NME VTX_SR.png', null, 'NME VTX', 1, 24);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (775, 'SR', '', 'Описание Шлем вратаря BAUER PROFILE 960XPM, топовая модель линейки PROFILE . Один из самых лучших вратарских шлемов и самый популярный шлем в NHL. Внешняя оболочка шлема выполнена из стеклопластика плотностью 12К и дополнена усиленной амортизирующей лентой 3М для максимального смягчения ударов шайбой. В качестве внутренней пены используется PORON XRD, которая обеспечивает максимальный комфорт и рассеивает энергию. Подбородник изготовлен из материала PORON XRD Особенности: • Уровень – Топовый • Внешнее покрытие – Карбон 12K; INNEGRA • Ключевые технологии – VN FOAM; PORON XRD • Внутренняя подкладка – 37,5 Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_PROFILE 960XPM_SR.png', null, 'PROFILE 960XPM', 1, 24);
