INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (778, 'YTH', '', 'Описание Блин с ловушкой CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Блин с ловушкой подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал комплекта SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Так же блин с ловушкой имеют очень легкий вес. Ловушка имеет невероятную гибкость, благодаря этому вратарь очень легко сможет ее сомкнуть. Конструкция ловушки состоит из двух частей для большей гибкости в области запястья. Угол закрытия ловушки – 60 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Конструкция блина позволяет вратарю точно и быстро перенаправить шайбу в углы. Блокировочная панель выполнена из пены низкой плотности и пластика, благодаря этому блин имеет легкий вес. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Возраст – Дети (4-7 лет) ✅Материал – SPEEDSKIN ✅Конструкция блина – Цельная ✅Ладонь - NASH ✅Конструкция ловушки – Двухсоставная, для большей гибкости. ✅Угол закрытия – 60 градусов Характеристики Возрастная группа YTH', '/root/images/Блин-ловушка вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (779, 'JR', '', 'Описание Блин с ловушкой BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Блин данной модели имеет новую форму, а блокировочная панель имеет монолитную конструкцию, которая повышает устойчивость блина и помогает вратарю отбивать шайбы в угол. Так же за счет монолитной конструкции блин имеет очень легкий вес и максимальное удобство. Особенности: ✅Уровень – Любитель ✅Манжета – Запатентованная технология FREE-FLEX ✅Ладонь - NASH ✅Лицевая сторона – Пена средней плотности; монолит ✅Подкладка – Влагоотталкивающий нейлон Ловушка линейки VAPOR сгибаются под углом 60 градусов и закрывается за счет смыкания всей ладони. Данная модель оснащена новой манжетой, которая обеспечивает большую амплитуду движения. При изготовлении ловушки использовались новые материалы, чтобы сделать перчатку гибкой и удобной. Помимо этого, была увеличены глубина кармана. Особенности: ✅Уровень – Любительский ✅Ключевые изменения – Новый глубокий карман; Новый ремень FAST STRAP; Манжета FREE-FLEX ✅Манжета – Монолит ✅Подкладка – NASH ✅Карман – Т-образный ✅Окантовка – Пена средней плотности Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Блин-ловушка вратаря/BAUER_VAPOR X2.7_JR.png', null, 'VAPOR X2.7', 1, 25);
