INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (781, 'SR', '', 'Описание Блин с ловушкой BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Блины линейки SUPREME отличаются монолитной конструкцией от основной плиты до торцевой части. Блин S27 оснащен гибкой манжетой FREE-FLEX, которая обеспечивает вратарю повышенную подвижность в области запястья, что позволяет эффективно отбивать шайбы в угол. Блин данной модели подходит для начинающих игроков и игроков-любителей. Особенности: • Уровень – Любитель • Манжета – Запатентованная технология FREE-FLEX • Ладонь - NASH • Лицевая сторона – Пена средней плотности; монолит • Подкладка – Влагоотталкивающий нейлон Ловушка S27 оснащена двойным Т-образным карманом и жесткой манжетой за счет этого она легко открывается и закрывается. Ловушка данной модели подходит для начинающих игроков и игроков-любителей. Особенности: • Уровень – Любительский • Манжета – Монолит • Подкладка – NASH • Карман – Двойной Т-образный • Окантовка – Пена средней плотности Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Блин-ловушка вратаря/BAUER_SUPREME S27_SR.png', null, 'SUPREME S27', 1, 25);
