INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (784, 'JR', '', 'Описание Блин с ловушкой CCM EXTREME FLEX 4.5, модель линейки EXTREME FLEX 2019 года. Конструкция блина позволяет вратарю точно и быстро перенаправить шайбу в углы. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. В области тыльной стороны используется пена низкой плотности с вставкой из пластика. В области пальцев для большей защиты используется пена средней плотности. Для более быстрой регулировки и максимального комфорта в области запястья CCM добавил быстросъемный ремешок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Любительский ✅Манжета – Сегментированная, регулируемая ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена низкой плотности; Пластик ✅Защита пальцев – Пена средней плотности Данная модель имеет цельную конструкцию манжеты и указательного пальца, что дает лучший контроль рикошета шайбы при попадании в ловушку. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Для большей защиты ладони используется войлок толщиной 6 мм и пена средней плотности. Для максимального комфорта и более мягкого ощущения при использовании в области пальцев используется материал NASH. Для более быстрой регулировки в области запястья CCM добавил быстросъемный ремешок. Угол закрытия – 75 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Особенности: ✅Уровень – Любительский ✅Ключевые изменения – Новый глубокий карман; Новый ремень для более быстрой регулировки; угол 75 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена низкой плотности Характеристики Возрастная группа JR Линейка EXTREME FLEX', '/root/images/Блин-ловушка вратаря/CCM_EXTREME FLEX E4.5_JR.png', null, 'EXTREME FLEX E4.5', 2, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (785, 'SR', '', 'Описание Блин с ловушкой CCM PREMIER P2.5, любительская модель линейки PREMIER 2018 года. Ловушка данной модели имеет цельную конструкцию и угол закрытия кармана 60 градусов. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для большей защиты в области ладони используется войлок плотностью 6 мм. Область манжеты выполнена из высококачественного полиэтилена, который максимально поглощает энергию удара. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Любительский ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена низкой плотности Блин данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для большей защиты тыльной стороны используется смесь из пен разной плотности. В области пальцев используется пена низкой плотности. Область запястья имеет конструкцию ONE-PIECE CUFF. Эта конструкция выполнена из пены, которая оборачивает полностью руку и заполняет негативное пространство. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Любительский ✅Манжета – ONE-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена разной плотности ✅Защита пальцев – Пена низкой плотности В сравнении с комплектом блин с ловушкой PREMIER P2.9, модель PREMIER P2.5 будет иметь менее качественную конструкцию с использованием материалов более низкого уровня. Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Блин-ловушка вратаря/CCM_PREMIER P2.5_SR.png', null, 'PREM