INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (794, 'SR', '', 'Описание Блин с ловушкой BAUER SUPREME 3S, полупрофессиональная модель линейки SUPREME 2020 года. Блины линейки SUPREME отличаются монолитной конструкцией от основной плиты до торцевой части. Новая форма блина адаптируется к индивидуальным особенностям кисти и позволяет легко управлять клюшкой. Манжета блина имеет более свободную форму для комфортного движения руки. Внешнее покрытие блина SHOCKLITE предотвращает возникновение вмятин. Для максимального удобства ладонь изготовлена из очень мягкого и надежного материала QUATTRO. Особенности: ✅Уровень – Полупрофессиональный ✅Ладонь - QUATTRO ✅Лицевая сторона – SHOCKLITE ✅Подкладка – THERMO MAX Ловушка оснащена двойным Т-образным карманом и жесткой манжетой за счет этого она легко открывается и закрывается. В области пальцев и манжете для более лучшей защиты используется композитный материал CURV. Внешний материал ловушки CATCHLITE способствует лучшему сцеплению с шайбой. Особенности: ✅Уровень – Полупрофессиональный ✅Манжета – Монолит ✅Подкладка – THERMO MAX ✅Карман – Двойной Т-образный ✅Внешний материал – CATCHLITE Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Блин-ловушка вратаря/BAUER_SUPREME 3S_SR.png', null, 'SUPREME 3S', 1, 25);
