INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (797, 'SR', '', 'Описание Блин с ловушкой CCM PREMIER II PRO, топовая модель линейки PREMIER 2018 года. Ловушка данной модели имеет цельную конструкцию и угол закрытия кармана 60 градусов. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для максимальной защиты в области ладони используется войлок плотностью 10 мм и пена D3O, которая поглощает энергию удара. Область манжеты выполнена из высококачественного вспененного полиэтилена. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Элитный ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена средней плотности Блин PREMIER II потерпел ряд ключевых изменений, по сравнению с предыдущей коллекцией. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для максимальной защиты тыльной стороны используется пена высокой плотности с вставкой из пластика. В области пальцев для большей защиты используется пена высокой плотности и пены D3O, которая максимально поглощает энергию удара. Область запястья имеет конструкцию ONE-PIECE CUFF. Эта конструкция выполнена из пены, которая оборачивает полностью руку и заполняет негативное пространство. Ладонь выполнена из материала NASH SURE-GRIP, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Элитный ✅Манжета – ONE-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена высокой плотности; Пластик ✅Защита пальцев – Пена высокой плотности; D3O Характеристики Возрастная группа SR Л