INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (824, 'JR', '', 'Описание Защита коленей вратаря CCM 1.5 JR Наколенники создают дополнительный уровень защиты коленного сустава. Высокопрочный нейлон используется в качестве материала внешней отделки наколенников. Легкая пена, формованная для достижения удобной посадки, используется для внутреннего наполнения и обеспечивает удобную посадку. Регулируемая система крепления имеет одни эластичный ремень шириной 5 см для фиксации на бедре и два эластичных ремня шириной 3,8 см для фиксации под коленом и ниже колена. Данный размер подходит на возраст от 9 до 14 лет Характеристики Возрастная группа JR', '/root/images/Защита коленей вратаря/CCM_1.5_JR.png', null, '1.5', 2, 29);
