INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (826, 'INT', '', 'Описание Защита коленей вратаря CCM 1.9 Наколенники имеют подвижную, двухкомпонентную конструкцию, которая не ограничивает движений вратаря. Внешняя отделка выполнена из высокопрочного нейлона, устойчивого к истиранию и механическим нагрузкам. Конструкция коленной чаши имеет глубокое посадочное место, что обеспечивает дополнительную поддержку колена. Формованная, коленная чаша выполнена из ударопрочного пластика, обеспечивая высокий уровень защиты. Внутренняя часть защиты колена выполнена из пены средней плотности, которая обеспечивает отличную защиту и имеет легкий вес. Данный размер подходит примерно на возраст от 11 до 15 лет Характеристики Возрастная группа INT', '/root/images/Защита коленей вратаря/CCM_1.9_INT.png', null, '1.9', 2, 29);
