INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (13, 'SR', '', 'Описание Коньки BAUER SUPREME S25, любительская модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Внешний материал этого конька изготовлен полиуретана 3-D TRUEFORM TECH PU. Двухсоставной язычок выполнен из войлока плотность 30 унции. Подошва изготовлена из термопластичной резины. Особенности: • Уровень – Любительский • Внешний материал – 3-D TRUEFORM TECH PU • Внутренний материал – MICROFIBER • Язык – Войлок плотностью 30 унций • Стелька – FORM-FIT EVA • Термоформуемость – Частичная формовка в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED PRO II • Лезвие – TUUK STAINLESS Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME S25_SR.png', null, 'SUPREME S25', 1, 1);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (14, 'YTH', '', 'Описание Коньки BAUER SUPREME 2S YTH, профессиональная, детская модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Внешний материал этого конька изготовлен из легкого, но довольно прочного композитного материала FIBER COMPOSITE. Внутренняя подкладка LOCK-FIT исключает любое проскальзывание стопы внутри ботинка при движении, что способствует более эффективной передаче усилия при движении. Язычок выполнен из войлока плотность 30 унции и имеет защиту плюсневой кости. Особенности: • Уровень – Профессиональный • Внешний материал – FIBER COMPOSITE • Внутренний материал – LOCK-FIT • Язык – Войлок плотностью 30 унций • Термоформуемость – Частичная формовка в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED PRO II • Лезвие – TUUK LS1 Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME 2S_YTH.png', null, 'SUPREME 2S', 1, 1);
