INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (16, 'YTH', '', 'Описание Коньки роликовые BAUER VAPOR RH XR300 YTH, модель линейки VAPOR 2018 года. Данная модель предназначена для игры в хоккей на роликах и активного отдыха. Внешний материал роликов выполнен из высококачественного нейлона с дополнительными вставками из пластика, который обеспечивает дополнительную защиту от порезов и придает ботинку максимальную жесткость. Язычок выполнен из войлока плотностью 30 унций. На роликах установлена высококачественная стальная рама HI-LO, которая имеет легкий вес и хорошую надежность. Колеса HI-LO жесткостью 82A, идеально подходят как для улицы, так и для закрытого помещения. Так же колеса имеют разный диаметр для максимально быстрого разгона и оптимального положения ноги. 3 колеса (2 передних – 59мм; 1 заднее – 68мм) Характеристики Возрастная группа YTH Линейка VAPOR', '/root/images/Коньки/BAUER_VAPOR RH XR300_YTH.png', null, 'VAPOR RH XR300', 1, 1);
