INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (22, 'JR', '', 'Описание Коньки BAUER NEXUS N2700, полупрофессиональная модель линейки NEXUS 2018 года. Конек имеет классическую посадку. Особенности этого конька в его комфорте. Внешний материал конька изготовлен из высокопрочного полиуретана 3-D Trueform tech PU. Язычок имеет двухкомпонентную конструкцию и выполнен из войлока плотностью 40 унций. Внутренняя подкладка сделана из материала гидрофобной микрофибры, которая отталкивает влагу. Подошва изготовлена из термопластичного полиуретана. Особенности: • Уровень – Любительский • Внешний материал – 3-D TRUEFORM TECH PU • Внутренний материал – HYDROPHOBIC MICROFIBER • Язык – Войлок 40 унций • Стелька – FORM-FIT EVA • Тормоформуемость – Частичная в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK S.STAINLESS Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Коньки/BAUER_NEXUS N2700_JR.png', null, 'NEXUS N2700', 1, 1);
