INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (24, 'SR', '', 'Описание Коньки BAUER SUPREME S170, полупрофессиональная модель в новой линейке SUPREME 2016 года. При изготовлении внешнего покрытия ботинка используется легкий материал 3D-lasted Tech Mesh, который обеспечивает высокую прочность, отличную жесткость и необходимую поддержку. Термоформируемый верх позволяет подобрать комфортную, анатомически правильную посадку. Набивка вокруг голеностопного сустава сделана из легкой пены с эффектом памяти, которая аккуратно облегает голеностоп, сохраняя форму и устраняя пустое пространство вокруг суставов. Внешняя пяточная чаша анатомической формы фиксирует пятку и обеспечивает оптимальную поддержку. Внутренний войлочный язычок Form-Fit, плотностью 48 oz, состоит из двух частей. Внутренняя набивка язычка из толстого слоя пены высокой плотности обеспечивает необходимую защиту передней части голеностопного сустава. Внутренняя подкладка выполнена из гидрофобной микрофибры, которая быстро отводит излишки влаги от стопы с последующим быстрым ее испарением, обеспечивая ногам комфортное состояние. Модернизированная стелька Form-Fit+ имеет противоскользящее покрытие, препятствуя проскальзыванию стопы внутри ботинка и способствуя эффективной передаче энергии при движении. Подошва Pro TPU обладает повышенной жесткостью. На коньках стоят стаканы Tuuk LightSpeed Edge, которые снабжены системой быстрой смены лезвий Trigger System. В стаканах используются лезвия из нержавеющей стали Tuuk LightSpeed LS.1. Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME S170_SR.png', null, 'SUPREME S170', 1, 1);
