INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (32, 'JR', '', 'Описание Коньки BAUER NEXUS N9000 профессиональная модель в линейке Nexus 2016 года. Затяните шнурки и приготовьтесь взять управление в свои руки благодаря усовершенствованному качеству прилегания коньков и глубокому заднику, который надежно удерживает пятку, обеспечивая непревзойденный контроль и мощь при каждом шаге. А благодаря ультралегкому композитному материалу CURV® и подстежке HYDRA MAX 2 ваши ступни будут надежно зафиксированы и останутся сухими, а последние шаги не уступят по силе первым. Прилегание. • Посадка коньков NEXUS - Хорошее прилегание от пятки до пальцев. Верхняя часть. • Конструкция Quarter Package - Передняя часть из композитного материала Curv® • Материал подкладки - Подкладка из сетчатого материала HYDRA MAX 2 • Защита лодыжки - Легкие, запоминающие форму тела подушки для лодыжек • Конструкция языка - Из 3 частей, вес 48 унций, из войлока, оснащен метатарзальной защитой • Стелька - FORM-FIT+ с хорошим захватом, защитой от влаги и стабилизатором пятки • Термоформуемость - Верхняя часть анатомической формы Нижняя часть. • Подошва - Pro TPU с графическим дизайном • Стакан - TUUK LIGHTSPEED EDGE • Лезвие - Нержавеющая сталь TUUK LS3 (размеры 4-12), нержавеющая сталь TUUK LS2 (размеры 1-3,5) Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Коньки/BAUER_NEXUS N9000_JR.png', null, 'NEXUS N9000', 1, 1);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (33, 'JR', '', 'Описание Коньки BAUER VAPOR X2.7, полупрофессиональная модель линейки VAPOR 2019 года. Конек данной модели выполнен по технологии ONE PIECE 3D POLYCARB QUARTER, благодаря чему ботинок лучше сидит в области лодыжки, а также отличается небольшим весом, обладает повышенным удобством и гибкостью. Внутренняя подкладка из мягкой микрофибры дополнительно повышает комфорт внутри конька. Коньки оснащены войлочным язычком плотностью 40 унций с защитной вставкой из пены высокой плотности, в области плюсневой кости, которая помогает достичь максимального комфорта, а также хороших защитных свойств. Лезвия данной модели выполнены из высококачественной и прочной нержавеющей стали. Особенности: ✅Уровень – Любительский ✅Внешний материал – ONE PIECE 3D POLYCARB QUARTER ✅Внутренний материал – MICROFIBER ✅Язык – Войлок плотностью 40 унций ✅Стелька – FORM FIT ✅Термоформуемость – Частичная формовка в области лодыжки ✅Подошва – TPU ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK STAINLESS Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Коньки/BAUER_VAPOR X2.7_JR.png', null, 'VAPOR X2.7', 1, 1);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (34, 'SR', '', 'Описание Коньки BAUER VAPOR S17 X700 - это полупрофессиональная модель в линейке VAPOR 2017 года. Готовы ли вы к скорости 360°? Тогда встречайте X700. Его волокнистый композит и верхняя часть обеспечивают маневренность, которая вам нужна, чтобы догнать шайбу, куда бы она ни летела, в то время как подкладка HYDRA MAX и легкая пена анатомической формы идеально прилегает к вашим ногам, обеспечивая защиту. Чистая скорость в любом направлении. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Волокнистый композитный верх с рифлением X-rib • Материал подкладки - Подкладка из сетчатого материала HYDRA MAX • Защита лодыжки - Легкие подушечки на лодыжке из пеноматериала анатомической формы • Конструкция языка - Войлочный язычок анатомической формы из трех частей FORM-FIT 48 унций с высокопрочной метатарзальной защитой • Стелька - FORM-FIT+ со стабилизатором • Термоформуемость - Термоформованная передняя часть • Поддержка пятки - Защищенная патентом интегрированная анатомическая поддержка пятки/лодыжки профессионального уровня НИЖНЯЯ ЧАСТЬ: • Подошва - Pro TPU с графическим дизайном • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - TUUK LS2 из нержавеющей стали Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Коньки/BAUER_VAPOR S17 X700_SR.png', null, 'VAPOR S17 X700', 1, 1);
