INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (77, 'YTH', '', 'Описание Наплечник BAUER SUPREME TOTAL ONE MX3 YTH, детская модель обеспечивает максимальное покрытие и эффективную защиту, имеет подвижную конструкцию и легкий вес. Панель защиты груди имеет сегментированное строение, которое позволяет младшим игрокам свободно двигаться без ущерба для защиты. Внутреннее наполнение передней панели выполнено из пены средней плотности. Центральный щиток, покрывающий и защищающий ключицы, грудину и верхнюю часть живота, имеет плавающую конструкцию и включает в себя пену высокой плотности и легковесную защитную пену Vent Armor, которая благодаря своей перфорированной структуре обладает эффективными защитными свойствами, легким весом и отличной вентиляцией. Окантовка вокруг горловины изготовлена из пены высокой плотности. Формованные пластиковые чаши, закрывающие плечевые суставы имеют удобную анатомическую форму и выстланы мягкой пеной. Односоставные элементы, защищающие бицепс, имеют регулируемую посадку и выполнены из вспененного материала с пластиковыми вставками. Задняя панель имеет мульти-сегментную конструкцию. Верхняя часть спины и элемент защиты позвоночника имеют внутреннюю набивку из пены высокой плотности. Пена средней плотности используется в защитных элементах, закрывающих область почек и нижних ребер. Внутренняя подкладка изготавливается из материала Thermo Max, который быстро отводит влагу и имеет антибактериальную пропитку, препятствуя образованию неприятных запахов. Нагрудник надежно крепится на теле маленького хоккеиста с помощью эластичных ремней, расположенных на талии и на бицепсах. Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME TOTALONE MX3_YTH.png', null, 'SUPREME TOTALONE MX3', 1, 3);
