INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (92, 'YTH', '', 'Описание Наплечник CCM SUPER TACKS AS1, детская профессиональная модель линейки TACKS 2019 года. Данный наплечник разработан специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Плечевые чаши наплечника выполнены из высококачественного полиэтилена, который обеспечивает максимальную защиту при минимальном весе. Для обеспечения высокого уровня защиты в области груди используется пена D3O, которая максимально поглощает энергию удара. Так же область груди имеет подвижную конструкцию, обеспечивая максимальный комфорт. Для защиты позвоночника используется пена высокой плотности. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – Высококачественный полиэтилен ✅Защита ключицы – Пена средней плотности ✅Защита груди – D3O ✅Защита позвоночника – Пена высокой плотности Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Нагрудник/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (93, 'YTH', '', 'Описание Наплечник BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди, для максимальной защиты, используется формованная пена средней плотности с вставкой из полиэтилена. Область бицепса имеет цельную конструкцию с защитной вставкой из формованного полиэтилена. Плечевые чаши выполнены из полиуретана. В качестве реберной защиты используется пена VENT ARMOR. В качестве внутренней подкладки используется материал THERMO MAX+, который эффективно отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – Формованный полиуретан ✅Защита груди – Формованная MD FOAM(пена средней плотности); полиэтилен ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – MD FOAM(пена средней плотности) ✅Защита позвоночника – MD FOAM(пена средней плотности); полиэтилен ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (94, 'JR', '', 'Описание Наплечник CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для игроков любителей. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP, которая рассеивает энергию удара. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пластик ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9040_JR.png', null, 'TACKS 9040', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (95, 'JR', '', 'Описание Наплечник WARRIOR ALPHA DX 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC PROTECTION – Формованная пластина в сочетании с пеной двойной плотности обеспечивает защиту в ключевых зонах • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа JR Линейка ALPHA', '/root/images/Нагрудник/WARRIOR_ALPHA DX3_JR.png', null, 'ALPHA DX3', 3, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (96, 'JR', '', 'Описание Наплечник BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данный наплечник предоставляет игроку хорошую защиту и максимальный комфорт. Смесь пены COMFORT EDGE и FLEXCELL, придают наплечнику плотное прилегание к телу игрока, обеспечивая подвижность и комфорт. В области бицепса используется смесь пены средней плотности и формованного полиэтилена, обеспечивая хорошие защитные свойства. Материал X-LITE SHIELD используется в области груди, обеспечивая максимальную защиты от любых ударов. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – Пена средней плотности ✅Защита груди- X-LITE SHIELD; плавающая конструкция ✅Передняя защита ребер – Перфорированная пена FLEXCELL ✅Защита спины – Перфорированная пена FLEXCELL ✅Защита позвоночника – Пена средней плотности; полиэтилен ✅Защита ключицы – Пена высокой плотности ✅Защита бицепса – Пена средней плотности; полиэтилен ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (97, 'JR', '', 'Описание Наплечник BAUER NEXUS 1N топовая модель в линейке Nexus 16 года. Традиционная посадка отлично сочетается с мультисегментной конструкцией нагрудника, гарантируя игроку высокий уровень подвижности защитного снаряжения и надежную защиту. Базовый слой нагрудника выполнен из пены средней плотности и разделен на сегменты для максимальной гибкости. Для создания дополнительной защиты ребер и области почек нагрудник усилен сегментами из пены высокой плотности. Подвижный щиток, закрывающий грудину, скомбинирован из пены высокой плотности, пластиковой вставки и формованной пены, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. В плечевых чашах, выполненных из литого пластика, так же используется пена с технологией Seven+, которая располагается в центре и обеспечивает эффективную защиту суставов. Съемная защита живота выполнена из пены средней плотности. Ворот нагрудника имеет мягкую окантовку из легкой пены, которая не травмирует кожу. Литой, двухсоставной, термоформируемый элемент защиты бицепса имеет свободную конструкцию Flex, которая обеспечивает максимальную мобильность этого элемента. Регулируемый ремень позволяет подобрать оптимальную длину элемента защиты бицепса. В задней части нагрудника используются перекрывающиеся сегменты из пены высокой плотности, которые проходят вдоль позвоночника, обеспечивая защиту профессионального уровня. Центральный щиток, закрывающий позвоночник, имеет широкую форму, перекрывая область сегментированной пены высокой плотности в базовом слое, что гарантирует высокий уровень защиты. Область ключиц и лопаток покрыта арочными элементами Free Glide Arch, при изготовлении которых используется пена высокой плотности. Арочные элементы движутся отдельно от базового слоя, обеспечивая игроку полную свободу движений. Внутренняя подкладка, выполненная по технологии 37.5, использует тепло тела хоккеиста для ускорения процесса испарения влаги и создания комфортного для игрока микроклимата. Специальная обработка материала подкладки исключает появление неприятных запахов. Эластичные ремни крепления с фиксацией на липучке позволяют закрепить нагрудник на талии и элементы защиты бицепса на руках игрока. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Нагрудник/BAUER_NEXUS 1N_JR.png', null, 'NEXUS 1N', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (98, 'SR', '', 'Описание Наплечник CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для игроков любителей. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP, которая рассеивает энергию удара. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пластик ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9040_SR.png', null, 'TACKS 9040', 2, 3);
