INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (131, 'YTH', '', 'Описание Наколенники CCM TACKS 9040 YTH, детская любительская модель линейки TACKS 2019 года. Данная модель предназначена для детей начального уровня. Наколенники имеют полностью симметричную дизайн без деления на правый и левый. Коленная чаша выполнена из пластика с применением дополнительного слоя пены изнутри, что позволяет избежать травм при падении. Защита голени выполнена из пластика с ребрами жесткости, что повышает ее защитные свойства. Надежная фиксация щитков на ноге возможна благодаря двум ремням. Особенности: ✅Уровень – Любительский ✅Защита колена - пластик с применением дополнительного слоя пены изнутри ✅Защита голени - Пластик ✅Крепление – Два ремня Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Щитки/CCM_TACKS 9040_YTH.png', null, 'TACKS 9040', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (132, 'YTH', '', 'Описание Наколенники WARRIOR ALPHA DX YTH, детская модель линейки ALPHA 2019 года. Основные особенности: ✅ DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты ✅ PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту ✅LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа YTH Линейка ALPHA', '/root/images/Щитки/WARRIOR_ALPHA DX_YTH.png', null, 'ALPHA DX', 3, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (133, 'YTH', '', 'Описание Наколенники BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Данная модель предназначена для детей начинающего уровня. Наколенники имеют анатомическую форму посадки. В области коленной чашки используется мягкая подкладка для комфорта ребенку. В качестве внутренней подкладки используется материал HYDROPHOBIC, который поглощает влагу и способствует быстрому высыханию. Особенности: • Уровень – Любительский • Посадка - Анатомическая • Защита голени – Ударопрочный пластик • Крепление – Y-образный ремень под коленом • Подкладка - HYDROPHOBIC Характеристики Возрастная группа YTH', '/root/images/Щитки/BAUER_NSX_YTH.png', null, 'NSX', 1, 4);
