INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (137, 'JR', '', 'Описание Наколенники BAUER NSX, любительская модель линейки NSX Основные защитные элементы созданы из ударопрочного пластика. Внутренняя подкладка выполнена из гидрофобной сетки для лучшего отвода влаги. Особенности: • Уровень – Любительский • Посадка - Клиновидная • Защита голени – Ударопрочный пластик • Защита икры – Пена средней плотности; ударопрочный пластик • Защита бедра – Пена • Крепление – Два ремня фиксируются сверху и в области икры • Подкладка - HYDROPHOBIC Характеристики Возрастная группа JR', '/root/images/Щитки/BAUER_NSX_JR.png', null, 'NSX', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (138, 'JR', '', 'Описание Наколенники CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. В области икроножной мышцы для больше защиты используется пена средней плотности и пластик. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Любительский ✅Защита икры – Пена средней плотности; пластик ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Щитки/CCM_TACKS 9040_JR.png', null, 'TACKS 9040', 2, 4);
