INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (140, 'YTH', '', 'Описание Наколенники CCM SUPER TACKS AS1 YTH, детская профессиональная модель линейки TACKS 2019 года. Данные наколенники разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Область колена и голени выполнена из формованного полиэтилена высокого качества, благодаря этому наколенники имеют легкий вес и хорошие защитные функции. Для максимального удобства и комфорта используется два ремня регулировки. Съемный внутренний вкладыш с пеной D3O в области коленной чашки, максимально защищает от прямых ударов и остается сухим во время игры. Особенности: ✅Уровень – Профессиональный ✅Защита колена – D3O ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Щитки/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (141, 'YTH', '', 'Описание Наколенники BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет анатомическую посадку. Для защиты колена и бедра используется пена средней плотности. Область икры выполнена из пены VENT ARMOR, которая придает наколеннику более легкий вес и хорошо поглощает энергию удара. Для максимального удобства, в качестве регулировки, используется мягкая эластичная липучка. Внутренняя подкладка выполнена по технологии THERMO MAX+, благодаря этому наколенник быстро сохнет. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита икры – VENT ARMOR ✅Боковая защита – Пена средней плотности ✅Защита колена – Пена средней плотности ✅Защита бедра – Пена средней плотности ✅Крепление – 1 эластичная широкая липучка ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (142, 'JR', '', 'Описание Наколенники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. В области икроножной мышцы использована формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется влагоотталкивающий материал. Особенности: • Уровень – Полупрофессиональный • Защита икры – Формованная пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка – PE FOAM Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Щитки/CCM_JETSPEED FT370_JR.png', null, 'JETSPEED FT370', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (143, 'SR', '', 'Описание Наколенники BAUER NSX, любительская модель линейки NSX Основные защитные элементы созданы из ударопрочного пластика. Внутренняя подкладка выполнена из гидрофобной сетки для лучшего отвода влаги. Особенности: • Уровень – Любительский • Посадка - Клиновидная • Защита голени – Ударопрочный пластик • Защита икры – Пена средней плотности; ударопрочный пластик • Защита бедра – Пена • Крепление – Два ремня фиксируются сверху и в области икры • Подкладка - HYDROPHOBIC Характеристики Возрастная группа SR', '/root/images/Щитки/BAUER_NSX_SR.png', null, 'NSX', 1, 4);
