INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (145, 'JR', '', 'Описание Наколенники BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данная модель наколенников сочетает в себе хорошую посадку и максимальную защиту в ключевых зонах. Новая конструкция в области голени X-LITE SHIELD, обеспечивает более глубокую и надежную посадку. Область икры выполнена из пены средней плотности и формованного полиэтилена, обеспечивая хорошую защиту. В области бедра используется гибкая пена средней плотности. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Клиновидная ✅Защита голени – X-LITE SHIELD ✅Защита икры – Пена средней плотности; Формованный полиэтилен ✅Боковая защита – Пена высокой плотности ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка - THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Щитки/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 4);
