INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (147, 'JR', '', 'Описание Наколенники BAUER NEXUS 1N - топовая модель в линейке NEXUS 16 года. В щитках 1N увеличена ширина защиты голени. Кроме того, в щитке используется встроенная накладка для защиты коленной чашечки и термоформируемая накладка для защиты голени. Защита икры — это та часть, где вы снова увидите использование технологии Seven + вместе с дополнительным PE вкладышем. Протектор бедра и основной вкладыш состоят из пены HD, в то время как боковые «крылья» на колене выполнены из пены MD с формованной PE вставкой. Фиксирует щиток система двойных ремней — ремень, который оборачивается вокруг щитка и новая регулируемая конструкция Bauer Tune Fit. Tune Fit обеспечивает более широкий диапазон для кастомизированной посадки, чтобы предотвратить перемещение или движение щитка во время игры. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Щитки/BAUER_NEXUS 1N_JR.png', null, 'NEXUS 1N', 1, 4);
