INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (151, 'JR', '', 'Описание Наколенники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры для большей защиты используется формованный полиэтилен. Для защиты бедра и колена используется пена средней плотности. Внутренняя подкладка THERMO MAX, которая отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита икры – Полиэтилен ✅Боковая защита – MD FOAM(пена средней плотности) ✅Защита бедра – MD FOAM(пена средней плотности) ✅Крепление – Два ремня фиксируются сверху и снизу Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (152, 'SR', '', 'Описание Наколенники BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данная модель наколенников сочетает в себе хорошую посадку и максимальную защиту в ключевых зонах. Новая конструкция в области голени X-LITE SHIELD, обеспечивает более глубокую и надежную посадку. Область икры выполнена из пены средней плотности и формованного полиэтилена, обеспечивая хорошую защиту. В области бедра используется гибкая пена средней плотности. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Клиновидная ✅Защита голени – X-LITE SHIELD ✅Защита икры – Пена средней плотности; Формованный полиэтилен ✅Боковая защита – Пена высокой плотности ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка - THERMO MAX Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Щитки/BAUER_VAPOR X2.9_SR.png', null, 'VAPOR X2.9', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (153, 'SR', '', 'Описание Наколенники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. В области икроножной мышцы использована формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется влагоотталкивающий материал. Особенности: • Уровень – Полупрофессиональный • Защита икры – Формованная пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка – PE FOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Щитки/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 4);
