INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (166, 'SR', '', 'Описание Наколенники BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2020 года. Данные наколенники обеспечивают полное покрытие голени, эффективность, комфорт и легкость. Новая конструкция в области голени X-LITE SHIELD, обеспечивает идеальную посадку. Область икры выполнена из высокотехнологичной пены HYPERLITE HD, обеспечивая лучшую защиту. В области бедра используется гибкая пена средней плотности. Внутренняя подкладка выполнена с применением технологии THERMO MAX+, которая эффективно отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Посадка - Клиновидная ✅Защита голени – X-LITE SHIELD ✅Защита икры – HYPERLITE ✅Боковая защита – Пена высокой плотности ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка - THERMO MAX+ Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Щитки/BAUER_VAPOR 2X_SR.png', null, 'VAPOR 2X', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (167, 'JR', '', 'Описание Наколенники CCM SUPER TACKS AS1, топовая модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку, для максимальной защиты в стратегически важных местах используется пена D3O, которая поглощает энергию удара. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Элитный ✅Защита икры – D3O; пена высокой плотности ✅Защита бедра – Пена высокой плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Щитки/CCM_SUPER TACKS AS1_JR.png', null, 'SUPER TACKS AS1', 2, 4);
