INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (185, 'YTH', '', 'Описание Шлем BAUER Prodigy с маской разработанный специально для детей. Лёгкое застегивание маски. Основа шлема выполнена из высокопрочного пластика с ребрами жесткости для обеспечения стабилизации формы шлема. Подкладка из пены двойной плотности для защиты головы и улучшенного комфорта. Подвижные дуги для ремня не мешают во время игры и не дают шлему слететь с головы. Размер шлема легко регулируется с помощью клипс-замков по бокам шлема. Шлем сертифицирован CSA, HECC, CE. Характеристики Возрастная группа YTH', '/root/images/Шлем/BAUER_PRODIGY YOUTH COMBO_YTH.png', null, 'PRODIGY YOUTH COMBO', 1, 5);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (186, 'UNDEFINED', '', 'Описание Шлем CCM TACKS 110, профессиональная модель линейки CCM . Данная модель имеет ударопрочный корпус, который создает оптимальный уровень защиты. Внутренняя подкладка выполнена из пены двойной плотности и отличается легким весом и отличными амортизирующими свойствами, которые эффективно гасят силу удара. Особенности: • Уровень – Профессиональный • Регулировка - Центральная, без использования инструментов • Ключевые технологии – Пена средней плотности • Сертифицирован – CSA, HECC и европейский знак соответствия требованиям безопасности CE Характеристики Линейка TACKS', '/root/images/Шлем/CCM_TACKS 110_UNDEFINED.png', null, 'TACKS 110', 2, 5);
