INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (198, 'UNDEFINED', '', 'Описание Шлем CCM TACKS 710, топовая модель линейки CCM 2018 года. Шлем был разработан для обеспечения максимального комфорта и удобства. Шлем имеет ударопрочный, поликарбонатный корпус. Первый слой внутренней подкладки выполнен по технологии R.E.D. SYSTEM, которая защищает от прямых и касательных ударов. Механизм защиты от ударов по касательной построен по принципу, частичного гашения силы удара при небольшом повороте системы амортизаторов относительно головы за счет емкостей с жидкостью. Внутренний слой, который соприкасается с головой, выполнен из пены I.Q.SHION, которая обеспечивает превосходный уровень комфорта благодаря эффекту памяти. Височная область выполнена из пены D3O, которая обладает высокой степенью поглощения ударного воздействия и уменьшает энергию удара во время игры. Особенности: • Уровень – Элитный • Регулировка - Центральная, без использования инструментов • Ключевые технологии – D3O; I.Q.SHION; R.E.D. SYSTEM • Сертифицирован – CSA, HECC и европейский знак соответствия требованиям безопасности CE Характеристики Линейка TACKS', '/root/images/Шлем/CCM_TACKS 710_UNDEFINED.png', null, 'TACKS 710', 2, 5);
