INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (217, 'YTH', '', 'Описание Налокотники BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Налокотники имеют цельную, односоставную конструкцию. Локтевая чаша выполнена из формованного пластика. Для максимального комфорта и более легкого веса в зоне предплечья используется пена средней плотности. Внутренняя подкладка HYDROPHOBIC поглощает влагу и способствует быстрому высыханию. Особенности: • Уровень – Любительский • Посадка - Клиновидная • Защита предплечья – Пена средней плотности • Крепление – Один V-образный • Подкладка – HYDROPHOBIC Характеристики Возрастная группа YTH', '/root/images/Налокотники/BAUER_NSX_YTH.png', null, 'NSX', 1, 7);
