INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (230, 'JR', '', 'Описание Налокотники BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данные налокотники предлагают максимальную амплитуду движений, защиту и комфорт на льду. Налокотники максимально подвижны и комфортны за счет новой трехсоставной конструкции. Так же они оснащены новой анатомически отформованной локтевой чашей из трех частей, обеспечивающей максимальный диапазон движений при сохранении безопасности на протяжении всей игры. Защитные элементы бицепса и предплечья выполнены из формованного полиэтилена. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Клиновидная ✅Защита локтя – Пена средней плотности ✅Защита бицепса – Полиэтилен ✅Защита предплечья – Полиэтилен ✅Крепление – Три ремня крепления ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 7);
