INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (233, 'JR', '', 'Описание Налокотники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пена высокой плотности ✅Защита бицепса – Пена высокой плотности ✅Защита предплечья – Пена высокой плотности ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 7);
