INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (250, 'SR', '', 'Описание Налокотники CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет легкий вес. Для защиты бицепса используется пена высокой плотности и формованный полиэтилен. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM, которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; Формованный полиэтилен • Защита бицепса – Формованный полиэтилен • Защита предплечья – Формованный полиэтилен • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Налокотники/CCM_JETSPEED FT390_SR.png', null, 'JETSPEED FT390', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (251, 'JR', '', 'Описание Налокотники BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности с добавлением полиэтилена. В области локтя используется пена IX-FOAM, которая поглощает энергию удара. В качестве внутренней подкладки используется материал THERMO MAX+, который максимально отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита локтя – IX-FOAM ✅Защита бицепса – Пена высокой плотности; Полиэтилен ✅Защита предплечья – Пена высокой плотности; Полиэтилен ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 2S_JR.png', null, 'SUPREME 2S', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (252, 'SR', '', 'Описание Налокотники BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Налокотники имеют улучшенное внешнее покрытие, если сравнивать с предыдущей линейкой NEXUS. Локтевая чаша выполнена из IX FOAM. В защите бицепса используется пена MDP со вставками из воздухопроницаемого пенного наполнителя VENT ARMOR, а внутренняя подкладка THERMO MAX с технологией SANITIZED®, предотвращает развитие бактерий и образование неприятного запаха. Особенности: • Уровень – Элитный • Посадка – Классическая, широкая • Защита локтя – IX FOAM • Защита бицепса – MDP; VENT ARMOR • Защита предплечья – MDP • Крепление – Три ремня крепления • Подкладка –THERMO MAX; SANITIZED®. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Налокотники/BAUER_NEXUS 2N_SR.png', null, 'NEXUS 2N', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (253, 'SR', '', 'Описание Налокотники BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Одним из главных достижений разработчиков этой модели можно считать снижение веса на 25% относительно предыдущей коллекции. Благодаря легким материалам и новейших технологий налокотники обеспечивают максимальное удобство и мобильность без ущерба защите. Применение в стратегических областях материала CURV® создает эффективную защиту с минимальным весом. Особенности: • Уровень – Элитный • Посадка - Клиновидная • Защита локтя – AEROLITE 2.0 • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – CURV® • Крепление – Три ремня крепления • Подкладка –37,5®; SANITIZED®. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR 1X LITE_SR.png', null, 'VAPOR 1X LITE', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (254, 'SR', '', 'Описание Налокотники BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2020 года. Данные налокотники предлагают максимальную амплитуду движений, защиту и комфорт на льду. Налокотники максимально подвижны и комфортны за счет новой трехсоставной конструкции. Защитные элементы бицепса и предплечья выполнены из пены HYPERLITE. Внутренняя подкладка выполнена с применением технологии THERMO MAX+, которая эффективно отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Посадка - Клиновидная ✅Защита локтя – Пена средней плотности ✅Защита бицепса – Пена HYPERLITE HD ✅Защита предплечья – Пена HYPERLITE; полиэтилен ✅Крепление – Три ремня крепления ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR 2X_SR.png', null, 'VAPOR 2X', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (255, 'SR', '', 'Описание Налокотники WARRIOR ALPHA DX, топовая модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • SLING WRAP - Регулируемые ремни обеспечивают надежную фиксацию и максимальное удобство • DX SHELL – Пена EVA, спрессованная по технологии SHELL SKIN, добавлена в ключевые зоны, чтобы обеспечить дополнительный уровень защиты там, где это необходимо больше всего • 3-PIECE – Новая трехкомпонентная конструкция, обеспечивает естественное движение и максимальную подвижность локтя • WARTECH FNC LINER – Антибактериальная подкладка Polygiene обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Налокотники/WARRIOR_ALPHA DX_SR.png', null, 'ALPHA DX', 3, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (256, 'JR', '', 'Описание Налокотники CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет очень легкий вес. Для защиты бицепса используется очень легкий композитный материал ROCKETFRAME. Зона предплечья выполнена из пены высокой плотности ZOTEFOAM, которая максимально защищает ваше тело. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Элитный • Защита локтя – JDP; Формованная пена PE • Защита бицепса – ROCKETFRAME • Защита предплечья – ZOTEFOAM • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Налокотники/CCM_JETSPEED FT1_JR.png', null, 'JETSPEED FT1', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (257, 'SR', '', 'Описание Налокотники CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Для защиты бицепса используется формованная пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O LITE. В области предплечья для максимальной защиты и удобства использована пена D3O и формованный полиэтилен. Для максимального комфорта в налокотниках используется новая инновационная технология фиксации. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; D3O LITE • Защита бицепса – Пена высокой плотности • Защита предплечья – Формованный полиэтилен; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Налокотники/CCM_TACKS 9080_SR.png', null, 'TACKS 9080', 2, 7);
