INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (259, 'JR', '', 'Описание Налокотники CCM SUPER TACKS AS1, топовая модель линейки CCM 2019 года. Данные налокотники предназначены для игроков элитного уровня и сочетает в себе максимальный комфорт и защиту. Для защиты бицепса используется формованная пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O. В области предплечья для максимальной защиты и удобства использована пена высокой плотности и пена D3O. Для максимального комфорта в налокотниках используется новая инновационная технология фиксации. Особенности: • Уровень – Элитный • Защита локтя – JDP; D3O • Защита бицепса – Пена высокой плотности • Защита предплечья – HD FOAM; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Налокотники/CCM_SUPER TACKS AS1_JR.png', null, 'SUPER TACKS AS1', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (260, 'JR', '', 'Описание Налокотники BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2020 года. Это самые легкие профессиональные налокотники на рынке. Благодаря технологиям COMFORT EDGE и SLEEVE FIT налокотники без усилий повторяют движения теля игрока. Налокотники максимально подвижны и комфортны за счет новой трехсоставной конструкции. Бицепс, предплечье и локтевая чаша выполнена из высокотехнологичной пены HYPERLITE HD, благодаря этому налокотники имеют очень легкий вес, при максимальной защите. Применение в стратегических областях материала CURV® создает эффективную защиту с минимальным весом. Особенности: ✅Уровень – Элитный ✅Посадка - Клиновидная ✅Защита локтя – HYPERLITE HD ✅Защита бицепса – Пена HYPERLITE с вставкой из полимера ✅Защита предплечья – CURV®; HYPERLITE HD ✅Крепление – Три ремня крепления ✅Подкладка – THERMO CORE Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR 2X PRO_JR.png', null, 'VAPOR 2X PRO', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (261, 'JR', '', 'Описание Налокотники BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из композитного материала CURV, который придает налокотнику легкий вес и отличные защитные свойства. В области локтя используется пена PORON XRD, которая максимально поглощает энергию удара. В качестве внутренней подкладки используется материал THERMO CORE, который максимально отводит влагу. Особенности: ✅Уровень – Элитный ✅Посадка - Анатомическая ✅Защита локтя – PORON XRD ✅Защита бицепса – CURV ✅Защита предплечья – CURV ✅Крепление – Три ремня крепления ✅Подкладка –THERMO CORE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 2S PRO_JR.png', null, 'SUPREME 2S PRO', 1, 7);
