INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (274, 'YTH', '', 'Описание Трусы BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Трусы предназначены для детей начинающего уровня. Для защиты позвоночника используется пена высокой плотности. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности. Защитные щитки, закрывающие бедра имеют цельную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. Внутренняя подкладка HYDROPHOBIC поглощает влагу и способствует быстрому высыханию. Трусы имеют классическую посадку. Особенности: ✅Уровень – Любительский ✅Удобство – Классическое ✅Защита позвоночника – Пена высокой плотности ✅Защита тазобедренная – Пена средней плотности; пластиковые вставки ✅Подкладка – HYDROPHOBIC ✅Регулировка – Отсутствует ✅Чехол – Плотность 400 ден Характеристики Возрастная группа YTH', '/root/images/Трусы/BAUER_NSX_YTH.png', null, 'NSX', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (275, 'YTH', '', 'Описание Трусы CCM SUPER TACKS 9040 YTH, детская любительская модель линейки TACKS 2019 года. Данная модель разработана специально для детей начального уровня и имеет анатомическую посадку. Чехол трусов выполнен из текстурированного полиэстера с эластичными вставками для большего комфорта. Для защиты позвоночника используется полиэтилен. В области бедра, почек и тазобедренных суставов используется пластик, чтобы обеспечить максимальную защиту для ребенка во время падений. Особенности: ✅Уровень – Любительский ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – Пластик ✅Защита бедра – Пластик ✅Регулировка – Отсутствует ✅Чехол – текстурированный полиэстер с эластичными вставками Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9040_YTH.png', null, 'TACKS 9040', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (276, 'JR', '', 'Описание Трусы BAUER VAPOR X800 LITE, полупрофессиональная модель линейки VAPOR 2018 года. Оптимальный выбор для любителей и детей. Отличное сочетание защитных функций и удобства. Внешний материал трусов изготовлен из прочного нейлона плотностью 400 ден. Зоны максимального износа дополнительно усилены нейлоном большей плотностью. Особенности: • Уровень – Полупрофессиональный • Удобство – Клиновидное • Защита позвоночника – Пена HYPERLITE; FREE FLEX • Защита почек – MD FOAM (пена средней плотности) • Защита тазобедренная – Пена; Пластиковые вставки • Защита бедра – Пена; пластиковые вставки • Подкладка – HYDROPHOBIC; SANITIZED® • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Трусы/BAUER_VAPOR X800 LITE_JR.png', null, 'VAPOR X800 LITE', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (277, 'SR', '', 'Описание Чехлы на трусы BAUER S19 SUPREME PANT SHELL, модель 2019 года. Внешний чехол выполнен из высокопрочного нейлона плотностью 840 ден. Область максимального износа дополнительно усилена. Конструкция чехла имеет регулировку по ширине штанин с помощью застежки-молнии. Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_S19 SUPREME PANT SHELL_SR.png', null, 'S19 SUPREME PANT SHELL', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (278, 'JR', '', 'Описание Трусы BAUER NSX , любительская модель линейки NSX 2018 года. Трусы предназначены для игроков-любителей. Для защиты позвоночника используется пена высокой плотности и вкладыш из ударопрочного полиэтилена. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют двухсоставную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. Особенности: • Уровень – Любительский • Удобство – Клиновидное • Защита позвоночника – Пена средней плотности MD • Защита почек – Пена высокой плотности HD • Защита тазобедренная – MD FOAM; пластиковые вставки • Подкладка – HYDROPHOBIC • Регулировка – Отсутствует • Чехол – Плотность 400 ден Характеристики Возрастная группа JR', '/root/images/Трусы/BAUER_NSX_JR.png', null, 'NSX', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (279, 'JR', '', 'Описание Трусы WARRIOR COVERT QRE 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • PREMIUM PROTECTION – Лучшая защитная пена и пластиковые вставки обеспечивают лучшую защиту • PREMIUM OUTER SHELL – Нейлоновая тянущаяся ткань 420 ден. • LINER – Специальная подкладка для лучшего впитывания пота. Характеристики Возрастная группа JR Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE3_JR.png', null, 'COVERT QRE3', 3, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (280, 'JR', '', 'Описание Трусы CCM JETSPEED FT350, любительская модель линейки CCM 2018 года. Трусы данной модели имеют чехол, выполненный из легкого полиэстера плотностью 210 ден. Защитные функции бедра выполнены из вспененного полиэтилена. Так же вставки из вспененного полиэтилена обеспечивают высокий уровень защиты в области почек и тазобедренного сустава. Внутренняя подкладка выполнена из полиуретана, которая обеспечивает комфорт. Особенности: • Уровень – Любительский • Удобство – Зауженное • Защита позвоночника – Вставка PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – Полиуретан • Регулировка – нет • Чехол – Плотность 210 ден Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT350_JR.png', null, 'JETSPEED FT350', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (281, 'YTH', '', 'Описание Трусы WARRIOR COVERT QRE YTH, детская профессиональная модель, линейки COVERT 2018 года. Основные особенности: ✅COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках ✅PREMIUM PROTECTION – Лучшая защитная пена и пластиковые вставки обеспечивают лучшую защиту ✅PREMIUM OUTER SHELL – Нейлоновая тянущаяся ткань 420 ден. ✅LINER – Специальная подкладка для лучшего впитывания пота. ОБРАТИТЕ ВНИМАНИЯ, ЧТО В ДАННОЙ МОДЕЛИ ПРИСУТСТВУЕТ РАЗМЕР YTH XL Характеристики Возрастная группа YTH Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE_YTH.png', null, 'COVERT QRE', 3, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (282, 'YTH', '', 'Описание Трусы BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет классическую посадку. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Одно из нововведения в этих трусах, стало то, что их можно отрегулировать на 1 дюйм(2,5см) Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 8);
