INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (286, 'SR', '', 'Описание Трусы BAUER NSX , любительская модель линейки NSX 2018 года. Трусы предназначены для игроков-любителей. Для защиты позвоночника используется пена высокой плотности и вкладыш из ударопрочного полиэтилена. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют двухсоставную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. Особенности: • Уровень – Любительский • Удобство – Клиновидное • Защита позвоночника – Пена средней плотности MD • Защита почек – Пена высокой плотности HD • Защита тазобедренная – MD FOAM; пластиковые вставки • Подкладка – HYDROPHOBIC • Регулировка – Отсутствует • Чехол – Плотность 400 ден Характеристики Возрастная группа SR', '/root/images/Трусы/BAUER_NSX_SR.png', null, 'NSX', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (287, 'YTH', '', 'Описание Трусы CCM TACKS AS1 YTH, детская профессиональная модель линейки TACKS 2019 года. Данный трусы разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Внешний материал трусов выполнен из нейлона плотностью 200 ден с усилением в ключевых зонах нейлоном плотностью 400 ден. Система регулировки по длине с помощью замка-молнии позволяет вам удлинить трусы на 1 дюйм(2,5см). Для обеспечения высокого уровня защиты в области копчика используется пена D3O, которая максимально поглощает энергию удара. Для защиты позвоночника, почек и тазобедренных суставов используется пена высокой плотности и пластик. Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Пена высокой плотности ✅Защита почек – Пластик ✅Защита тазобедренная – Пена высокой плотности; Пластик ✅Защита бедра – Пластик; Пена высокой плотности ✅Защита копчика – D3O ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 200 ден с усилением в ключевых зонах нейлоном плотностью 400 ден Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Трусы/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (288, 'JR', '', 'Описание Трусы CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Трусы данной модели имеют чехол, выполненный из текстурированного полиэстера плотностью 400 ден. Защитные функции бедра выполнены из вспененного полиэтилена. Так же вставки из вспененного полиэтилена обеспечивают высокий уровень защиты в области почек и тазобедренного сустава. Особенности: • Уровень – Полупрофессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – Влагоотталкивающий полиэстер • Регулировка – нет • Чехол – Плотность 400 ден Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT370_JR.png', null, 'JETSPEED FT370', 2, 8);
