INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (300, 'SR', '', 'Описание Трусы CCM SUPER TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется формованный полиэтилен. В области бедра и почек для максимальной защиты используется пластик. Для защиты тазобедренных суставов используется конструкция JDP, которая рассеивает удар. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – JDP ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9060_SR.png', null, 'TACKS 9060', 2, 8);
