INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (302, 'JR', '', 'Описание Трусы CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Данные трусы имеют очень легкий вес и зауженную посадку, для лучшей мобильности и комфорта. В стратегически важных местах, таких как, тазобедренные суставы, позвоночник, бедро используется полиэтилен, который максимально защищает от ударов. Внутренняя подкладка DRYFORM, которая эффективно отталкивает влагу. Чтобы подобрать идеальную форму шорты имеют регулируемую посадку и могут удлиняться на 1« (2,5 см) за счет вшитой под поясом молнии. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Особенности: • Уровень – Профессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – DRYFOAM • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден; усиленные вставки в области бедра Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (303, 'SR', '', 'Описание Трусы BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX, который отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Подкладка – THERMO MAX ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME S29_SR.png', null, 'SUPREME S29', 1, 8);
